import { useEffect, useCallback } from "react";
import { useQuery, useMutation } from "convex/react";
import { useNavigate, useLocation } from "@tanstack/react-router";
import { api } from "@hikai/convex";
import { useUserPreferences } from "@/domains/core/hooks";
import { Id } from "@hikai/convex/convex/_generated/dataModel";

/**
 * Hook para gestionar la organizacin actual del usuario.
 *
 * - Lee currentOrgId del store (persistido en localStorage)
 * - Auto-selecciona la primera org (personal) si ninguna est seleccionada
 * - Sincroniza entre pestaas automticamente
 * - Al cambiar de org en rutas de productos, navega a /products
 */
export function useCurrentOrg() {
  const {
    currentOrgId,
    setCurrentOrgId,
    setCurrentProductId,
    isReady: isPrefsReady,
  } = useUserPreferences();
  const navigate = useNavigate();
  const location = useLocation();

  const organizations = useQuery(
    api.organizations.organizations.getUserOrganizationsWithDetails
  );

  // Mutation para trackear acceso a org
  const updateLastOrgAccess = useMutation(
    api.userPreferences.updateLastOrgAccess
  );

  // Auto-seleccionar primera org si ninguna est seleccionada
  // IMPORTANTE: Solo ejecutar cuando isPrefsReady=true para evitar sobrescribir
  // valores que an no se han cargado de localStorage
  useEffect(() => {
    if (isPrefsReady && !currentOrgId && organizations && organizations.length > 0) {
      // Preferir org personal si existe (debera ser la primera por el sort)
      const personal = organizations.find((o) => o.isPersonal);
      setCurrentOrgId(personal?._id ?? organizations[0]._id);
    }
  }, [isPrefsReady, currentOrgId, organizations, setCurrentOrgId]);

  // Encontrar la org actual basndose en el ID
  const currentOrg =
    organizations?.find((o) => o._id === currentOrgId) ?? null;

  // Cambiar org con navegacin a /products si estamos en una ruta de productos
  const setCurrentOrg = useCallback(
    (orgId: string) => {
      setCurrentOrgId(orgId);

      // Limpiar producto actual al cambiar de org (productos son especficos de org)
      setCurrentProductId(null);

      // Trackear acceso a la org (no bloquea UX si falla)
      updateLastOrgAccess({
        organizationId: orgId as Id<"organizations">,
      }).catch((error) => {
        console.error("Error tracking org access:", error);
      });

      // Si estamos en una ruta de productos, navegar a /products
      if (location.pathname.startsWith("/products")) {
        navigate({ to: "/products" });
      }
    },
    [setCurrentOrgId, setCurrentProductId, updateLastOrgAccess, navigate, location.pathname]
  );

  return {
    currentOrg,
    organizations: organizations ?? [],
    isLoading: !isPrefsReady || organizations === undefined,
    setCurrentOrg,
  };
}

